mapper.insert:
insert into p_w_game (id, user_id, game_time, 
  overplus_times, user_nick, voucher_flag, 
  create_time, update_time, rank
  )
values (#{id,jdbcType=BIGINT}, #{userId,jdbcType=VARCHAR}, #{gameTime,jdbcType=BIGINT}, 
  #{overplusTimes,jdbcType=BIGINT}, #{userNick,jdbcType=VARCHAR}, #{voucherFlag,jdbcType=BIGINT}, 
  #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}, #{rank,jdbcType=BIGINT}
  )

mapper.select:
  SELECT * FROM (
	SELECT @rownum:=@rownum+1 AS rownum, Wgame.*
	FROM (SELECT @rownum:=0) r, p_W_game Wgame order by game_time asc) A WHERE A.user_id=#{userId};
